class WeLearnUser(HttpUser):
    wait_time = between(5, 10)

    def on_start(self):
        self.client.headers = {"X-API-Key": "welearn"}

    # @task(1)
    def search(self):
        lang = random.choice(["en", "fr"])
        corpora = random.sample(corpus, random.randint(1, 4))
        self.client.post(
            "/api/v1/search/by_document",
            json={"corpora": corpora},
            params={
                "query": " ".join(random.sample(paragraphs[lang], 2)),
                "nb_results": 10,
            },
            name=f"/search/{lang}/{'&'.join(corpora)}",
        )

    # @task(1)
    def search_slices(self):
        lang = random.choice(["en", "fr"])
        self.client.post(
            "/api/v1/search/by_slices",
            params={
                "query": " ".join(random.sample(paragraphs[lang], 2)),
                "nb_results": 10,
                "concatenate": True,
            },
            name=f"/search/by_slices/{lang}",
        )

    @task(2)
    def chat(self):
        lang = random.choice(["en", "fr"])
        query = random.choice(questions[lang])
        reformulated_query = query + " " + random.choice(string.ascii_letters)*5  # Add some noise to bypass caching
        with self.client.post(
            "/api/v1/search/by_slices",
            json={"sdg_filter": [], "query": reformulated_query, "corpora": corpus},
            params={"nb_results": 10, "concatenate": True},
            name=f"Search by slices ({lang})",
            catch_response=True,
        ) as resp2:
            try:
                sources = json.loads(resp2.text)
            except json.decoder.JSONDecodeError:
                raise Exception("Sources could not be retrieved")
        self.client.post(
            "/api/v1/qna/chat/answer",
            json={"sources": sources, "query": reformulated_query},
            name=f"Chat answer ({lang})",
        )

    @task(2)
    def syllabus(self):
        lang = random.choice(["en", "fr"])
        self.client.post(
            "/api/v1/tutor/syllabus",
            json=syllabus_payload,
            params={"lang": lang},
            name=f"Syllabus generation ({lang})",
        )
